CREATE DATABASE "application-image";
CREATE TABLE auteurs (IdAuteur SERIAL PRIMARY KEY, Nom VARCHAR (20), Prenom VARCHAR (20));

CREATE TABLE orientations (IdOrientation SERIAL PRIMARY KEY, Orientation VARCHAR (20));

CREATE TABLE images(IdImage SERIAL PRIMARY KEY, Nom VARCHAR (20), date DATE, IdOrientation INT, Fichier VARCHAR (20), Likes INT DEFAULT 0, IdAuteur INT, CONSTRAINT "fk_Orientation_orientations" FOREIGN KEY (IdOrientation) REFERENCES orientations(IdOrientation), CONSTRAINT "fk_IdAuteur_auteurs" FOREIGN KEY (IdAuteur) REFERENCES auteurs(IdAuteur));


CREATE TABLE commentaires (IdCommentaire SERIAL PRIMARY KEY, IdImage INT, Texte VARCHAR (300), CONSTRAINT "fk_IdImage_images" FOREIGN KEY (IdImage) REFERENCES images(IdImage));
